import React, { useContext, useState } from "react";
import { invoiceContext } from "../../../../store/invoiceStore";
import "./PaymentTerms.css";

function PaymentTerms() {
  const [purple1, setPurple1] = useState(false);
  const { invoiceState, updateInvoice } = useContext(invoiceContext);

  function updatePaymentTerms(e) {
    updateInvoice({
      type: "UPDATE_PAYMENT_TERMS",
      displayMessage: e.target.textContent,
      numOfDays: +e.target.id,
    });
    console.log(invoiceState.paymentTerms);
  }

  function clickHandler(e) {
    updatePaymentTerms(e);
    if (e.target.id == 1) {
      setPurple1(true);
    }
  }
  return (
    <div className="payment-terms-box">
      <div className="net-box">
        <p
          onClick={clickHandler}
          id="1"
          className={purple1 ? "net-text purple-text" : "net-text"}
        >
          Net 1 Day
        </p>
      </div>
      <div className="net-box">
        <p onClick={clickHandler} id="7" className="net-text">
          Net 7 Days
        </p>
      </div>
      <div className="net-box">
        <p onClick={clickHandler} id="14" className="net-text">
          Net 14 Days
        </p>
      </div>
      <div className="net-box">
        <p onClick={clickHandler} id="30" className="net-text">
          Net 30 Days
        </p>
      </div>
    </div>
  );
}

export default PaymentTerms;
