import React, { createContext, useReducer } from "react";

const initialState = [
  {
    quanity: "1",
    unitPrice: "1",
    itemName: "1",
    subtotal: "1",
  },
];

const reducer = (state, action) => {
  switch (action.type) {
    case "itemName": {
      console.log("I'm in itemName");
      console.log(state);
      console.log(action.index);
      console.log(action.value);
      return [(state[action.index].itemName = action.value)];
    }

    case "quanity": {
      return [
        ...state,
        ((state[action.index].quanity = +action.value),
        (state[action.index].subtotal =
          +action.value * +state[action.index].unitPrice)).toFixed(2),
      ];
    }

    case "unitPrice": {
      return [
        ...state,
        ((state[action.index].unitPrice = action.value),
        (state[action.index].subtotal =
          +state[action.index].quanity * +action.value)).toFixed(2),
      ];
    }

    default:
      return state;
  }
};

export const lineItemContext = createContext(initialState);

export const LineItemStore = ({ children }) => {
  const [lineItemState, updateLineItem] = useReducer(reducer, initialState);

  return (
    <lineItemContext.Provider value={{ lineItemState, updateLineItem }}>
      {children}
    </lineItemContext.Provider>
  );
};
