import React, { useState, useReducer } from "react";

import Card from "../components/Card";
import LoginButton from "../components/LoginButton";
import LoginInput from "../components/LoginInput";
import LoginInputForm from "../components/LoginInputForm";

import "./Login.css";

const Login = () => {
  const initialValues = {
    firstName: "",
    lastName: "",
    emailAddress: "",
    password: "",
    confirmPassword: "",
  };

  const [loginIsActive, setLoginIsActive] = useState(true);
  const [formValues, setFormValues] = useReducer(
    (curVals, newVals) => ({ ...curVals, ...newVals }),
    initialValues
  );

  const { firstName, lastName, emailAddress, password, confirmPassword } =
    formValues;

  function clickHandler(e) {
    if (e.target.textContent === "Login" && !loginIsActive) {
      setLoginIsActive(true);
    } else if (
      e.target.textContent === "Signup" ||
      e.target.textContent === "Signup now"
    ) {
      setLoginIsActive(false);
    }
  }

  function handleFormChange(e) {
    const { name, value } = e.target;
    setFormValues({ [name]: value });
  }

  function submitHandler(e) {
    e.preventDefault();
    console.log(e.target);
  }

  return (
    <div className="login-page">
      <Card>
        <h1>{loginIsActive ? "Login Form" : "Signup Form"}</h1>
        <div className="login-btns-container">
          <div className="btn-container" onClick={clickHandler}>
            <LoginButton classes={loginIsActive ? "active" : "not-active"}>
              Login
            </LoginButton>
          </div>
          <div className="btn-container" onClick={clickHandler}>
            <LoginButton classes={loginIsActive ? "not-active" : "active"}>
              Signup
            </LoginButton>
          </div>
        </div>
        <form>
          {!loginIsActive && (
            <LoginInput
              type={"text"}
              name={"firstName"}
              placeholder={"First Name"}
              value={firstName}
              onChange={handleFormChange}
            />
          )}
          {!loginIsActive && (
            <LoginInput
              type={"text"}
              placeholder={"Last Name"}
              name={"lastName"}
              value={lastName}
              onChange={handleFormChange}
            />
          )}

          <LoginInput
            type={"text"}
            placeholder={"Email Address"}
            name={"emailAddress"}
            value={emailAddress}
            onChange={handleFormChange}
          />
          <LoginInput
            type={"password"}
            placeholder={"Password"}
            name={"password"}
            value={password}
            onChange={handleFormChange}
          />
          {!loginIsActive && (
            <LoginInput
              type={"password"}
              placeholder={"Confirm Password"}
              name={"confirmPassword"}
              value={confirmPassword}
              onChange={handleFormChange}
            />
          )}
        </form>
        <div className="big-btn">
          <LoginButton classes={"active"}>
            {loginIsActive ? "Login" : "Signup"}
          </LoginButton>
        </div>

        <div className="bottom-text">
          <p>Not a member?</p>{" "}
          <p className="signup-text" onClick={clickHandler}>
            Signup now
          </p>
        </div>
      </Card>
    </div>
  );
};

export default Login;
