import React, { useReducer } from "react";
import LoginButton from "../components/LoginButton";
import LoginInput from "../components/LoginInput";

const LoginFormContainer = ({ loginIsActive }) => {
  const initialValues = {
    firstName: "",
    lastName: "",
    emailAddress: "",
    emailAddressError: false,
    password: "",
    confirmPassword: "",
  };

  const [formValues, setFormValues] = useReducer(
    (curVals, newVals) => ({ ...curVals, ...newVals }),
    initialValues
  );

  const {
    firstName,
    lastName,
    emailAddress,
    emailAddressError,
    password,
    passwordError,
    confirmPassword,
  } = formValues;

  function resetForm() {
    setFormValues({
      firstName: "",
      lastName: "",
      emailAddress: "",
      emailAddressError: false,
      password: "",
      passwordError: false,
      confirmPassword: "",
    });
  }

  function handleFormChange(e) {
    const { name, value } = e.target;
    setFormValues({ [name]: value });

    if (name === "emailAddress") {
      setFormValues({ emailAddressError: false });
    }
    if (name === "password") {
      setFormValues({ passwordError: false });
    }
  }

  function submitHandler(e) {
    e.preventDefault();
    if (loginIsActive) {
      if (!emailAddress.includes("@")) {
        setFormValues({ emailAddressError: true });
      }
      if (password.length === 0) {
        setFormValues({ passwordError: true });
      }
    } else {
      console.log("I'm submitted");
    }
  }
  return (
    <form onSubmit={submitHandler}>
      {!loginIsActive && (
        <LoginInput
          type={"text"}
          name={"firstName"}
          placeholder={"First Name"}
          value={firstName}
          onChange={handleFormChange}
        />
      )}
      {!loginIsActive && (
        <LoginInput
          type={"text"}
          placeholder={"Last Name"}
          name={"lastName"}
          value={lastName}
          onChange={handleFormChange}
        />
      )}

      <LoginInput
        type={"text"}
        placeholder={"Email Address"}
        name={"emailAddress"}
        value={emailAddress}
        onChange={handleFormChange}
        className={emailAddressError ? "input error" : "input"}
      />
      {emailAddressError && (
        <p className="error-message">Valid email required</p>
      )}
      <LoginInput
        type={"password"}
        placeholder={"Password"}
        name={"password"}
        value={password}
        onChange={handleFormChange}
      />
      {passwordError && (
        <p className="error-message">Please enter your password</p>
      )}
      {!loginIsActive && (
        <LoginInput
          type={"password"}
          placeholder={"Confirm Password"}
          name={"confirmPassword"}
          value={confirmPassword}
          onChange={handleFormChange}
        />
      )}
      <div className="big-btn">
        <LoginButton classes={"active"}>
          {loginIsActive ? "Login" : "Signup"}
        </LoginButton>
      </div>
    </form>
  );
};

export default LoginFormContainer;
