import React, { createContext, useReducer } from "react";

const initialState = [
  {
    quanity: "",
    unitPrice: "",
    itemName: "",
    subtotal: "",
  },
];

const reducer = (state, action) => {
  switch (action.type) {
    case "itemName": {
      return [...state, (state[action.index].itemName = action.value)];
    }

    case "quanity": {
      let total = +action.value * +state[action.index].unitPrice;

      return [
        ...state,
        (state[action.index].quanity = +action.value),
        (state[action.index].subtotal = total.toFixed(2)),
      ];
    }

    case "unitPrice": {
      let total = +state[action.index].quanity * +action.value;
      return [
        ...state,
        (state[action.index].unitPrice = action.value),
        (state[action.index].subtotal = total.toFixed(2)),
      ];
    }

    default:
      return state;
  }
};

export const lineItemContext = createContext(initialState);

export const LineItemStore = ({ children }) => {
  const [lineItemState, updateLineItem] = useReducer(reducer, initialState);

  return (
    <lineItemContext.Provider value={{ lineItemState, updateLineItem }}>
      {children}
    </lineItemContext.Provider>
  );
};
