import React, { useReducer } from "react";
import LoginButton from "../components/LoginButton";
import LoginInput from "../components/LoginInput";

const LoginFormContainer = ({ loginIsActive }) => {
  const initialValues = {
    firstName: "",
    lastName: "",
    emailAddress: "",
    password: "",
    confirmPassword: "",
  };

  const [formValues, setFormValues] = useReducer(
    (curVals, newVals) => ({ ...curVals, ...newVals }),
    initialValues
  );

  const { firstName, lastName, emailAddress, password, confirmPassword } =
    formValues;

  function handleFormChange(e) {
    const { name, value } = e.target;
    setFormValues({ [name]: value });
  }

  let emailError = false;

  function submitHandler(e) {
    e.preventDefault();
    if (loginIsActive) {
      if (!emailAddress.includes("@")) {
        emailError = true;
      }
    } else {
      console.log("I'm submitted");
    }
  }
  return (
    <form onSubmit={submitHandler}>
      {!loginIsActive && (
        <LoginInput
          type={"text"}
          name={"firstName"}
          placeholder={"First Name"}
          value={firstName}
          onChange={handleFormChange}
        />
      )}
      {!loginIsActive && (
        <LoginInput
          type={"text"}
          placeholder={"Last Name"}
          name={"lastName"}
          value={lastName}
          onChange={handleFormChange}
        />
      )}

      <LoginInput
        type={"text"}
        placeholder={"Email Address"}
        name={"emailAddress"}
        value={emailAddress}
        onChange={handleFormChange}
      />
      {emailError && <p>Valid email required</p>}
      <LoginInput
        type={"password"}
        placeholder={"Password"}
        name={"password"}
        value={password}
        onChange={handleFormChange}
      />
      {!loginIsActive && (
        <LoginInput
          type={"password"}
          placeholder={"Confirm Password"}
          name={"confirmPassword"}
          value={confirmPassword}
          onChange={handleFormChange}
        />
      )}
      <div className="big-btn">
        <LoginButton classes={"active"}>
          {loginIsActive ? "Login" : "Signup"}
        </LoginButton>
      </div>
    </form>
  );
};

export default LoginFormContainer;
