import React, { useState } from "react";
import Card from "../components/Card";
import LoginButton from "../components/LoginButton";
import LoginInput from "../components/LoginInput";
import LoginInputForm from "../components/LoginInputForm";
import "./Login.css";

const Login = () => {
  const [loginIsActive, setLoginIsActive] = useState(true);
  const [signupIsActive, setSignupIsActive] = useState(false);

  function loginClickHandler() {
    console.log("We have arrived");
  }

  return (
    <div className="login-page">
      <Card>
        <h1>Login Form</h1>
        <div className="login-btns-container">
          <div className="btn-container" onClick={loginClickHandler}>
            <LoginButton>Login</LoginButton>
          </div>
          <div className="btn-container">
            <LoginButton>Signup</LoginButton>
          </div>
        </div>
        <LoginInputForm>
          <LoginInput type={"text"} placeholder={"Email Address"} />
          <LoginInput type={"password"} placeholder={"Password"} />
        </LoginInputForm>
        <LoginButton classes={"active"}>Login</LoginButton>

        <div className="bottom-text">
          <p>Not a member?</p> <p className="signup-text">Signup now</p>
        </div>
      </Card>
    </div>
  );
};

export default Login;
