import React, { useContext, useEffect, useState } from "react";
import { lineItemContext } from "../../../../store/lineItemStore";
import "./ItemInForm.css";
import icon_delete from "../../../../assets/icon_delete.svg";

function ItemInForm({ index }) {
  const { lineItemState, updateLineItem } = useContext(lineItemContext);
  const [weAreEditing, setWeAreEditing] = useState(false);

  useEffect(() => {
    if (lineItemState.selectedLineItems.length > 0) {
      setWeAreEditing(true);
      console.log("This is the items: ", lineItemState.selectedLineItems);
    }
  }, []);

  function formChangeHandler(e) {
    updateLineItem({
      type: e.target.name,
      value: e.target.value,
      index,
    });
  }
  function deleteHandler() {
    updateLineItem({
      type: "DELETE_LINE",
      index,
    });
  }

  return (
    <div className="input-container">
      <label htmlFor="item-name" className="label bill-to-label">
        Item Name
      </label>
      <input
        type="text"
        className="invoice-input bill-to-input"
        name="itemName"
        value={
          weAreEditing
            ? lineItemState.selectedLineItems[index].item_name
            : lineItemState.lineItems[index].itemName
        }
        onChange={formChangeHandler}
      />
      <div className="quantity-price-container">
        {weAreEditing && "Stephen is awesome"}
        <div className="qty-container">
          <label htmlFor="quantity" className="label bill-to-label item-label">
            Qty.
          </label>
          <input
            type="number"
            min="0"
            className="invoice-input bill-to-input qty-input"
            name="quantity"
            value={
              weAreEditing
                ? lineItemState.selectedLineItems[index].quantity
                : lineItemState.lineItems[index].quantity
            }
            onChange={formChangeHandler}
          />
        </div>
        <div className="price-container">
          <label htmlFor="price" className="label bill-to-label">
            Price
          </label>
          <input
            type="text"
            className="invoice-input bill-to-input"
            name="unitPrice"
            value={
              weAreEditing
                ? lineItemState.selectedLineItems[index].unit_price
                : lineItemState.lineItems[index].unitPrice
            }
            onChange={formChangeHandler}
          />
        </div>
        <div className="total-container">
          <div className="total-container-2">
            <p className="label bill-to-label">Total</p>
            <div className="total-display">
              {lineItemState.lineItems[index].subtotal}
            </div>
          </div>
          <img
            src={icon_delete}
            alt="trash can"
            onClick={deleteHandler}
            className="trash-can"
          />
        </div>
      </div>
    </div>
  );
}

export default ItemInForm;
