import React, { createContext, useReducer } from "react";

const initialState = {
  user: null,
  modalToDisplay: null,
  paymentTermOptions: false,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "USER_LOGGED_IN": {
      return {
        ...state,
        user: action.value,
      };
    }

    case "USER_CREATED": {
      return {
        ...state,
        modalToDisplay: "userCreated",
      };
    }

    case "NEW_INVOICE_MODAL": {
      return {
        ...state,
        modalToDisplay: "newInvoice",
      };
    }

    case "CLOSE_MODAL": {
      return {
        ...state,
        modalToDisplay: null,
      };
    }

    case "SELECT_PAYMENT_TERMS": {
      if (paymentTermOptions) {
        return {
          ...state,
          paymentTermOptions: false,
        };
      } else {
        return {
          ...state,
          paymentTermOptions: true,
        };
      }
    }

    default:
      return state;
  }
};

export const userContext = createContext(initialState);

export const Store = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <userContext.Provider value={{ state, dispatch }}>
      {children}
    </userContext.Provider>
  );
};
